var path = require('path'),
    fs = require('fs');


function fixture(name) {
  var fixtureName = name + '.xml';
  var fixturePath = path.join(__dirname, 'fixtures', fixtureName);
  var options = {flag: 'r', encoding: 'utf8'};
  return fs.readFileSync(fixturePath, options);
}


module.exports = {
  fixture: fixture
};
